mapper.select:
select Count(userId)
from member
where userId=#{userId}

select Count(userId)
from member
where userId=#{userId} and userPw=#{userPw}

select userId,userPw
from member
where userId=#{userId} and userPw=#{userPw}

select Count(userName)
from member 
where userName=#{userName} and userEmail=#{userEmail}

select userId,date
from member
where userName=#{userName} and userEmail=#{userEmail}

select Count(userId)
from member 
where userId=#{userId}

select userEmail
from member
where userId=#{userId}
